import React, { useState } from 'react';
import axios from 'axios';

function GenCert() {
  const [privateKey, setPrivateKey] = useState(null);
  const [inputText, setInputText] = useState(null);
  const [certificate, setCertificate] = useState('');

  const handlePrivateKeyChange = (e) => {
    setPrivateKey(e.target.files[0]);
  };

  const handleInputChange = (e) => {
    setInputText(e.target.files[0]);
  };

  const generateCertificate = async () => {
    try {
      const formData = new FormData();
      formData.append('private_key', privateKey);
      formData.append('input_text', inputText);
      console.log("Going to Flask")
      const response = await axios.post('https://192.168.2.241:5000/g', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setCertificate(response.data.certificate);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1>Generate Certificate</h1>
      <label for="Private_Key">Upload Private key file:</label>
      <input type="file" id="Private_Key" onChange={handlePrivateKeyChange} accept=".pem" />
      <label for="inputtxt">Upload initial cert file file:</label>
      <input type="file" id="inputtxt" onChange={handleInputChange} accept=".txt" />
      {/* <textarea
        placeholder="Input Text"
        value={inputText}
        onChange={handleInputTextChange}
      ></textarea> */}
      <button onClick={generateCertificate}>Generate Certificate</button>
      {certificate && (
        <div className='flex flex-col items-center justify-center p-10 border-2'>
          <h2>Certificate:</h2>
          <p>{certificate}</p>
        </div>
      )}
    </div>
  );
}

export default GenCert;
