import React, { useState } from 'react';
import axios from 'axios';
import './Aadhar.css';
function Aadhar() {
  const [file, setFile] = useState(null);
  const [name, setName] = useState('');
  const [dob, setDob] = useState('');
  const [result, setResult] = useState('');
  const [errorFlag, setErrorFlag] = useState(false);

  const handleFileChange = (event) => {
    console.log(event.target.files[0]);
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('image', file);

    try {
      const response = await axios.post('https://localhost:5000/process_image', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      const apiName = response.data.name;
      const apiDob = response.data.dob;

      if (apiName === name && apiDob === dob) {
        setResult('Valid');
      } else {
        setResult('inValid');
      }
      setErrorFlag(false)
    } catch (error) {
      setErrorFlag(true)
      console.error('Error processing image:', error);
    }
  };

  return (
    <div className="Aadhar">
      <h1>Aadhaar Card verification</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" accept="image/*" onChange={handleFileChange} />
        <label>
          Enter Name:
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </label>
        <label>
          Enter Date of Birth(yyyy-mm-dd):
          <input type="text" value={dob} onChange={(e) => setDob(e.target.value)} />
        </label>
        <button type="submit">Submit for verification</button>
      </form>
      <div>
      {result && !errorFlag && (
        <div>
          <h2>Status: {result}</h2>
        </div>
      )}
      {errorFlag && (
        <div>
          <h2>UNABLE TO GET THE AADHAR </h2>
        </div>
      )}
      </div>

    </div>
  );
}

export default Aadhar;
