import React, { useState } from 'react';
import PropTypes from 'prop-types';
import './Login.css';

async function loginUser(username, password) {
  const credentials = { username, password };
  console.log(credentials);
  const response = await fetch('https://localhost:8080/login', {
    method: 'POST',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(credentials),
  });

  if (response.ok) {
    console.log('Request was made successfully.');
  } else {
    console.log('Request was not made successfully.');
  }

  return response.json();
}


async function getServerStatus() {
  try {
    const response = await fetch('https://localhost:8080/', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });

    if (response.ok) {
      console.log('Request was made successfully.');
      const data = await response.text(); // Parse JSON response
      console.log('Response data:', data); // Log the parsed data
      return data;
    } else {
      console.log('Request was not made successfully.');
      return null;
    }
  } catch (error) {
    console.error('An error occurred:', error);
    return null;
  }
}



export default function Login({ setToken }) {
  const [username, setUserName] = useState();
  const [password, setPassword] = useState();
  const handleSubmit = async e => {
    e.preventDefault();
    // const status = await getServerStatus();
    // console.log(status);
    const token = await loginUser(username,password);
    setToken(token);
  }

  return(
    <div className="login-wrapper">
      <h1>Please Log Ins</h1>
      <form onSubmit={handleSubmit}>
        <label>
          <p>Username</p>
          <input type="text" onChange={e => setUserName(e.target.value)} />
        </label>
        <label>
          <p>Password</p>
          <input type="password" onChange={e => setPassword(e.target.value)} />
        </label>
        <div>
          <button type="submit">Submit</button>
        </div>
      </form>
    </div>

  )
}

Login.propTypes = {
  setToken: PropTypes.func.isRequired
};
